const input=document.getElementById("input"),resultado=document.getElementById("resultado");document.getElementById("validar").addEventListener("click",()=>{const e=input.value.match(/^\/(.*)\/$/);if(e){const t=new RegExp(e[1]);Swal.fire({title:t.test(resultado.value)?"Éxito":"Error",text:t.test(resultado.value)?"¡La expresión y el valor hacen match!":"Error: No hay coincidencias.",icon:t.test(resultado.value)?"success":"error",confirmButtonText:"Aceptar"})}else Swal.fire({title:"Error",text:"La expresión regular debe estar entre barras diagonales",icon:"error",confirmButtonText:"Aceptar"})}),document.getElementById("limpiar").addEventListener("click",()=>{input.value="",resultado.value=""});